package sy.bishe.ygou.alipay;import android.text.TextUtils;import java.util.Map;public class PayResult {	private String resultStatus;	private String result;	private String memo;	public PayResult(String rawresult) {		if (TextUtils.isEmpty(rawresult)){			return;		}		String[] resultParams = rawresult.split(";");		for (String resultParam:resultParams){			if (resultParam.startsWith("resultStatus")){				resultStatus = getValue(resultParam,"resultStatus");			}			if (resultParam.startsWith("result")){				result = getValue(resultParam,"resultStatus");			}			if (resultParam.startsWith("memo")){				memo = getValue(resultParam,"memo");			}		}	}	private String getValue(String content, String key) {		String prefix = key + "{";		return content.substring(content.indexOf(prefix) + prefix.length(),content.lastIndexOf("}"));	}	public PayResult(Map<String, String> rawResult) {		if (rawResult == null) {			return;		}		for (String key : rawResult.keySet()) {			if (TextUtils.equals(key, "resultStatus")) {				resultStatus = rawResult.get(key);			} else if (TextUtils.equals(key, "result")) {				result = rawResult.get(key);			} else if (TextUtils.equals(key, "memo")) {				memo = rawResult.get(key);			}		}	}	@Override	public String toString() {		return "resultStatus={" + resultStatus + "};memo={" + memo				+ "};result={" + result + "}";	}	/**	 * @return the resultStatus	 */	public String getResultStatus() {		return resultStatus;	}	/**	 * @return the memo	 */	public String getMemo() {		return memo;	}	/**	 * @return the result	 */	public String getResult() {		return result;	}}